mapper.select:
SELECT d.discuss_id,d.discuss_title,s.stu_name FROM discuss d INNER JOIN student s ON d.stu_id = s.stu_id WHERE d.STATUS = 1
AND ( d.discuss_title LIKE '%${keyWord}%' OR s.stu_name LIKE '%${keyWord}%')
ORDER BY d.discuss_id DESC LIMIT #{pageParam.startIndex},#{pageParam.rows}

SELECT * FROM ( SELECT d.discuss_id,d.discuss_title,d.discuss_content,d.discuss_date,s.stu_name, COUNT(*) AS 'count' FROM discuss d LEFT JOIN reply_discuss rd ON d.discuss_id = rd.discuss_id LEFT JOIN student s ON d.stu_id = s.stu_id WHERE d.status = 1
AND ( d.discuss_title LIKE '%${keyWord}%' OR s.stu_name LIKE '%${keyWord}%')
GROUP BY d.discuss_id ) t ORDER BY YEAR(t.discuss_date) DESC,MONTH(t.discuss_date) DESC,t.count DESC LIMIT #{pageParam.startIndex},#{pageParam.rows}

SELECT d.discuss_id,d.discuss_title,d.discuss_content,d.discuss_date,s.stu_name,s.stu_id FROM discuss d 
INNER JOIN student s ON d.stu_id = s.stu_id
WHERE d.STATUS = 1 AND d.discuss_id = #{id}

SELECT COUNT(*) FROM ( SELECT d.discuss_id,d.discuss_title,d.discuss_date,s.stu_name FROM discuss d INNER JOIN student s ON d.stu_id = s.stu_id WHERE d.STATUS = 1
AND ( d.discuss_title LIKE '%${keyWord}%' OR s.stu_name LIKE '%${keyWord}%')
) t

SELECT d.discuss_id,d.discuss_title FROM discuss d LEFT JOIN attention_discuss ad ON d.discuss_id = ad.discuss_id LEFT JOIN student s ON ad.stu_id = s.stu_id WHERE ad.stu_id = #{id}
AND d.discuss_title LIKE '%${keyWord}%'
GROUP BY d.discuss_id LIMIT #{pageParam.startIndex},#{pageParam.rows}

SELECT COUNT(*) FROM ( SELECT d.discuss_title FROM discuss d LEFT JOIN attention_discuss ad ON d.discuss_id = ad.discuss_id LEFT JOIN student s ON ad.stu_id = s.stu_id WHERE ad.stu_id = #{id}
AND d.discuss_title LIKE '%${keyWord}%'
GROUP BY d.discuss_id ) t

SELECT COUNT(*) FROM student s 
INNER JOIN report_discuss rd ON  rd.stu_id = s.stu_id
WHERE s.stu_id = #{stuid} AND rd.discuss_id = #{discussid}
