select.if:
SELECT c.stu_id,c.stu_name,c.stu_sex,CASE c.stu_sex WHEN 1 THEN '男' WHEN 0 THEN '女' END AS 'sex_name',c.role_name FROM ( SELECT s.stu_id,s.stu_name,s.stu_sex,r.role_name FROM community_role cr INNER JOIN role r ON cr.role_id = r.role_id INNER JOIN stu_community_role scr ON scr.community_role_id = cr.community_role_id INNER JOIN student s ON scr.stu_id = s.stu_id where cr.community_id = 1 ) c
and (CONCAT(c.stu_id,'') LIKE '%${keyWord}%' OR c.stu_name LIKE '%${keyWord}%' OR c.sex_name LIKE '%${keyWord}%') OR r.role_name LIKE '%${keyWord}%'
LIMIT ${pageParam.startIndex},${pageParam.rows}

SELECT * from ( SELECT *, case WHEN grade = 0 then '一年级' WHEN grade = 1 then '二年级' WHEN grade = 2 then '三年级' WHEN grade = 3 then '四年级' ELSE '已毕业' END AS 'grade_name' FROM ( SELECT s.*,cls.classes,cls.grade,CASE s.stu_sex WHEN 1 then '男' WHEN 0 then '女' end as 'sex_name' FROM classes cls INNER JOIN student s on cls.classes_id = s.classes_id ) t ) c
WHERE c.stu_name like '%${keyWord}%' OR CONCAT(c.stu_birthday,'') like '%${keyWord}%' OR c.sex_name like '%${keyWord}%'
LIMIT ${pageParam.startIndex},${pageParam.rows}

SELECT COUNT(*) FROM ( SELECT c.stu_id,c.stu_name,c.stu_sex,CASE c.stu_sex WHEN 1 then '男' WHEN 0 THEN '女' END AS 'sex_name',c.role_name FROM ( SELECT s.stu_id,s.stu_name,s.stu_sex,r.role_name FROM community_role cr INNER JOIN role r ON cr.role_id = r.role_id INNER JOIN stu_community_role scr ON scr.community_role_id = cr.community_role_id INNER JOIN student s ON scr.stu_id = s.stu_id WHERE cr.community_id = 1 ) c
AND (CONCAT(c.stu_id,'') LIKE '%${keyWord}%' OR c.stu_name LIKE '%${keyWord}%' OR c.sex_name LIKE '%${keyWord}%') OR r.role_name LIKE '%${keyWord}%'
) t

mapper.select:
SELECT * from ( SELECT s.*, CASE s.stu_sex WHEN 1 then '男' WHEN 0 THEN '女' END AS 'sex_name' FROM student s ) c WHERE c.classes_id IS NULL
CONCAT(c.stu_id,'') LIKE '%${keyWord}%' OR c.stu_name LIKE '%${keyWord}%' OR CONCAT(c.stu_birthday,'') LIKE '%${keyWord}%' OR c.sex_name LIKE '%${keyWord}%'
LIMIT ${pageParam.startIndex},${pageParam.rows}

SELECT s.*,cls.classes,cls.grade
FROM  classes cls INNER JOIN student s ON cls.classes_id = s.classes_id
WHERE s.stu_id = #{id}

SELECT COUNT(*) FROM  student 
where classes_id IS NOT NULL
AND stu_id = #{stuid} AND stu_pwd = #{stuPwd}

SELECT COUNT(*) FROM  student 
where classes_id IS  NULL
AND stu_id = #{stuid} AND stu_pwd = #{stuPwd}
